{
	int *pVertIndexY;
	Point2I *pDir;
	Point2I *pPoint;
	uint j;
	float *pVertPosZ;
	float maxZ;
	float minZ;
	int k;
	uint i;
	Point2I DIRECTIONS [4];
	Point2I VERT_INDICES [6];
	Vector3F vertPoses [4];
	bool bCheckMinMax;
	
	VERT_INDICES[2].y = 3;
	VERT_INDICES[4].y = 3;
	VERT_INDICES[5].y = 3;
	DIRECTIONS[0].x = 0;
	DIRECTIONS[0].y = 0;
	DIRECTIONS[1].x = 1;
	DIRECTIONS[1].y = 0;
	DIRECTIONS[2].x = 1;
	DIRECTIONS[2].y = 1;
	DIRECTIONS[3].x = 0;
	DIRECTIONS[3].y = 1;
	VERT_INDICES[0].x = 0;
	VERT_INDICES[0].y = 1;
	VERT_INDICES[1].x = 0;
	VERT_INDICES[1].y = 2;
	VERT_INDICES[2].x = 0;
	VERT_INDICES[3].x = 1;
	VERT_INDICES[3].y = 2;
	VERT_INDICES[4].x = 1;
	VERT_INDICES[5].x = 2;
	bCheckMinMax = false;
	minZ = 10000.0;
	maxZ = -10000.0;
	i = 0;
	if (shapeCount != 0) {
		pPoint = shapePoints + 1;
		do {
			if (((i == shapeCount - 1) || (shapePoints->x != pPoint->x)) || (shapePoints->y != pPoint->y))
			{
				Map3D_GetBlockVertexPositions(map,shapePoints->x,shapePoints->y,vertPoses);
				j = 0;
				pVertIndexY = &VERT_INDICES[0].y;
				do {
					if (buildMaxVariation < ABS(vertPoses[pVertIndexY[-1]].z - vertPoses[*pVertIndexY].z)) {
						return 0;
					}
					j += 1;
					pVertIndexY = pVertIndexY + 2;
				} while (j < 6);
				pVertPosZ = &vertPoses[0].z;
				pDir = DIRECTIONS;
				k = 4;
				do {
					if ((map->blocks3D[(pDir->y + shapePoints->y) * map->gridWidth + pDir->x + shapePoints->x]
							 .flags3D & MAP3DBLOCK_FLAG_VERTEXLEVELED) != MAP3DBLOCK_FLAG_NONE) {
						if (*pVertPosZ < minZ) {
							minZ = *pVertPosZ;
						}
						if ((ushort)((ushort)(*pVertPosZ < maxZ) << 8 | (ushort)(*pVertPosZ == maxZ) << 0xe) ==
								0) {
							maxZ = *pVertPosZ;
						}
						bCheckMinMax = true;
					}
					pDir = pDir + 1;
					pVertPosZ = pVertPosZ + 3;
					k += -1;
				} while (k != 0);
			}
			else {
				i += 1;
				pPoint = pPoint + 1;
				shapePoints = shapePoints + 1;
			}
			i += 1;
			pPoint = pPoint + 1;
			shapePoints = shapePoints + 1;
		} while (i < shapeCount);
	}
						// if (bCheckMinMax && ((maxZ - minZ) > buildTolerance))
	if ((bCheckMinMax) &&
		 ((ushort)((ushort)(maxZ - minZ < buildTolerance) << 8 |
							(ushort)(maxZ - minZ == buildTolerance) << 0xe) == 0)) {
		return FALSE;
	}
	return TRUE;
}



